(1, 1, 'Master', NULL, NULL, '<script>\n    // 이미지 입력 요소 및 미리보기 이미지 태그 선택\n    const imageInput = document.getElementById(\'user_image\');\n    const imagePreview = document.getElementById(\'image-preview\');\n    const tooltipIcon = document.getElementById(\'tooltip-icon\');\n    \n    const title = document.getElementById(\'title\');\n    const celebName = document.getElementById(\'celeb-name\');\n    const productName = document.getElementById(\'product-name\');\n    const brandName = document.getElementById(\'brand-name\');\n    const productUrl = document.getElementById(\'product-url\');\n    const linkPositionX = document.getElementById(\'link-position-x\');\n    const linkPositionY = document.getElementById(\'link-position-y\');\n    \n    console.log(imageInput);\n    console.log(imagePreview);\n\n    // 파일 선택 시 이벤트 리스너 추가\n    imageInput.addEventListener(\'change\', function(event) {\n        const file = event.target.files[0]; // 업로드된 첫 번째 파일\n\n        if (file) {\n            const reader = new FileReader();\n\n            // 파일 로드 완료 시 실행\n            reader.onload = function(e) {\n                imagePreview.src = e.target.result; // 이미지 src 설정\n                imagePreview.style.display = \'block\'; // 이미지 표시\n            };\n\n            // 파일을 읽어 데이터 URL로 변환\n            reader.readAsDataURL(file);\n        }\n    });\n        \n   imagePreview.addEventListener(\"click\", \n   function(event) {\n      \n      // 이미지 내에서의 상대적인 좌표 계산\n      const rect = \n      imagePreview.getBoundingClientRect();\n\n      // X상대좌표\n      const x = event.clientX - rect.left; \n      // Y상대좌표\n      const y = event.clientY - rect.top; \n\n      // 좌표 값 전송\n      console.log(x);\n      console.log(y);\n      \n      tooltipIcon.style.left = `${x}px`;\n      tooltipIcon.style.top = `${y}px`;\n      \n      linkPositionX.value = x;\n      linkPositionY.value = y;\n   });\n</script>', 0, NULL),
